import React, { useState } from "react";

function TaskList({ tasks, onDelete, onToggleComplete, onUpdate }) {
  const [editId, setEditId] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editDescription, setEditDescription] = useState("");

  const startEditing = (task) => {
    setEditId(task.id);
    setEditTitle(task.title);
    setEditDescription(task.description);
  };

  const saveEdit = () => {
    onUpdate(editId, { title: editTitle, description: editDescription });
    setEditId(null);
  };

  const cancelEdit = () => {
    setEditId(null);
  };

  return (
    <ul className="list-group">
      {tasks.map((task) => (
        <li
          key={task.id}
          className={`list-group-item d-flex justify-content-between align-items-center ${
            task.completed ? "list-group-item-success" : ""
          }`}
        >
          <div className="d-flex align-items-center gap-2" style={{ flex: 1 }}>
            <input
              type="checkbox"
              checked={task.completed}
              onChange={() => onToggleComplete(task.id, !task.completed)}
            />
            {editId === task.id ? (
              <div className="flex-grow-1">
                <input
                  className="form-control mb-1"
                  value={editTitle}
                  onChange={(e) => setEditTitle(e.target.value)}
                />
                <input
                  className="form-control"
                  value={editDescription}
                  onChange={(e) => setEditDescription(e.target.value)}
                />
              </div>
            ) : (
              <span
                style={{
                  textDecoration: task.completed ? "line-through" : "none",
                  flex: 1,
                  marginLeft: "0.5rem",
                }}
              >
                <strong>{task.title}</strong>: {task.description}
              </span>
            )}
          </div>

          <div>
            {editId === task.id ? (
              <>
                <button className="btn btn-success btn-sm me-1" onClick={saveEdit}>
                  Save
                </button>
                <button className="btn btn-secondary btn-sm me-1" onClick={cancelEdit}>
                  Cancel
                </button>
              </>
            ) : (
              <>
                <button
                  className="btn btn-warning btn-sm me-1"
                  onClick={() => startEditing(task)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => {
                    if (window.confirm("Are you sure to delete this task?")) {
                      onDelete(task.id);
                    }
                  }}
                >
                  Delete
                </button>
              </>
            )}
          </div>
        </li>
      ))}
    </ul>
  );
}

export default TaskList;
