import React, { useEffect, useState } from "react";
import axios from "axios";

import TaskForm from "./TaskForm";
import TaskList from "./TaskList";
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  const [filter, setFilter] = useState("all"); // all, completed, pending
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    const res = await axios.get("http://localhost:5000/tasks");
    setTasks(res.data);
  };

  const addTask = async () => {
    if (title && description) {
      await axios.post("http://localhost:5000/tasks", {
        title,
        description,
        completed: false,
      });
      setTitle("");
      setDescription("");
      fetchTasks();
    }
  };

  const deleteTask = async (id) => {
    await axios.delete(`http://localhost:5000/tasks/${id}`);
    fetchTasks();
  };

  const toggleComplete = async (id, completed) => {
    await axios.put(`http://localhost:5000/tasks/${id}`, { completed });
    fetchTasks();
  };

  const updateTask = async (id, updatedFields) => {
    await axios.put(`http://localhost:5000/tasks/${id}`, updatedFields);
    fetchTasks();
  };

  const filteredTasks = tasks
    .filter((task) => {
      if (filter === "completed") return task.completed;
      if (filter === "pending") return !task.completed;
      return true;
    })
    .filter((task) => {
      const term = searchTerm.toLowerCase();
      return (
        task.title.toLowerCase().includes(term) ||
        task.description.toLowerCase().includes(term)
      );
    });

  return (
    <div className="container py-4">
      <h2 className="mb-4">Task Manager</h2>

      <input
        type="text"
        className="form-control mb-3"
        placeholder="Search tasks..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <div className="mb-3">
        <button
          className={`btn me-2 ${filter === "all" ? "btn-primary" : "btn-outline-primary"}`}
          onClick={() => setFilter("all")}
        >
          All
        </button>
        <button
          className={`btn me-2 ${filter === "completed" ? "btn-primary" : "btn-outline-primary"}`}
          onClick={() => setFilter("completed")}
        >
          Completed
        </button>
        <button
          className={`btn ${filter === "pending" ? "btn-primary" : "btn-outline-primary"}`}
          onClick={() => setFilter("pending")}
        >
          Pending
        </button>
      </div>

      <TaskForm
        title={title}
        description={description}
        setTitle={setTitle}
        setDescription={setDescription}
        onAdd={addTask}
      />
      <TaskList
        tasks={filteredTasks}
        onDelete={deleteTask}
        onToggleComplete={toggleComplete}
        onUpdate={updateTask}
      />
    </div>
  );
}

export default App;
